import {
  Component,
  Directive,
  ElementRef,
  EventEmitter,
  Input,
  Output,
} from '@angular/core';

@Component({
  selector: 'ngoffline',
  template: `
    <div id="box--ngoffline" class="box" *ngIf="!onLine" (click)="onLine = true">
      <div class="centreMessage">
        <div class="close">
          <img [src]="closeIcon" />
        </div>
        <div class="text">
          <img [src]="img" class="icon"/>
          <div>{{ msg }}</div>
        </div>
      </div>
    </div>
  `,
  styleUrls:['./ngoffline.component.scss']
})
export class NgOfflineComponent {
  @Input('message') msg: string = 'You are not connected';
  @Output('status') cnx = new EventEmitter<boolean>(true);
  closeIcon = "data:image/png;base64,";
  img =
    'data:image/png;base64,';

  onLine: boolean = false; // is en ligne ðŸ”¥

  constructor() {
    window.addEventListener('online', (): void => {
      // ðŸ”¥ we're back online!
      this.onLine = true;
      this.cnx.emit(true);
    });

    window.addEventListener('offline', (): void => {
      // ðŸ˜± oh no!
      this.onLine = false;
      this.cnx.emit(false);
    });
  }
}

@Directive({
  selector: '[ngoffline]',
})
export class NgOfflineDirective {
  _el: any;
  onLine: boolean = true;
  constructor(el: ElementRef) {
    this._el = el;
    this.goOnline();
    window.addEventListener('online', (): void => {
      // ðŸ”¥ we're back online!
      this.goOnline();
    });

    window.addEventListener('offline', (): void => {
      // ðŸ˜± oh no!
      this.goOffline();
    });
  }

  public goOnline() {
    this._el.nativeElement.style.display = 'none';
    this.onLine = true;
  }

  public goOffline() {
    this._el.nativeElement.style.display = 'inherit';
    this.onLine = false;
  }
}
